import React from 'react'

const Card = (prop) => {
  let badgeText
  if (prop.openSpots === 0) {
    badgeText = 'sold out'
  } else if (prop.openSpots > 0) {
    badgeText = 'online'
  }
  return (
    <div className="card">
        <div className='card--img'>
          <img src={`../images/${prop.img}`} alt="img"/>
            <span>{badgeText}</span>
        </div>

        <div className='card--ratings'>
            <i class='bx bxs-star'></i>
            <span>{prop.rating}</span>
            <span>{prop.reviewCount} *</span>
            <span>{prop.country}</span>
        </div>

        <p>{prop.title}</p>
        <p><strong>From ${prop.price}</strong> / person</p>
    </div>
  )
}

export default Card
